INSERT INTO "public"."am_public_operator" ("id", "version", "app_id", "available", "created_by_user", "created_by_name", "created_date", "last_modified_by_user", "last_modified_by_name", "last_modified_date", "org_id_path", "compute_spec_id", "container_image_id", "container_image_name", "defalut_file", "description", "dev_language_type", "fork_count", "inference_file_path", "is_dierctory", "is_distributed", "is_internet", "is_train_model", "is_train_visual", "model_type", "name", "operator_classify", "operator_type", "p_id", "reality_name", "save_status", "source_id", "source_type", "start_file_path", "summary", "train_break_point", "train_distributed_parameter", "train_log", "train_model_parameter", "train_visual_parameter", "am_operator_parameter_id") VALUES (19, 3, 1, 't', 1, '超级管理员', '1900-01-20 09:11:46.831937', 1, '超级管理员', '2023-12-20 15:14:33.354136', '0/1/', 1, 1, NULL, NULL, NULL, 'PYTHON', 2, '/inference.py', 'f', 'f', NULL, 't', 't', NULL, 'ResNet图像分类算法', 'PICTURE_CLASSIFY', 'SYSTEM_PRESET', 4, NULL, 1, NULL, 'LOCAL_UPLOAD', '/train.py', '<ul><li><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;"> &nbsp; &nbsp; &nbsp; &nbsp;ResNet</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">（</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">Residual Neural Network</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">）是一种深度卷积神经网络（</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">CNN</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">）架构，用于图像分类任务。</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">ResNet</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">的设计目标是解决深层网络训练中的梯度消失和网络退化问题。它通过引入残差连接（</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">residual connection</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">）和残差模块（</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">residual block</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">）来实现对深层网络的有效训练。</span></li><li><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;"> &nbsp; &nbsp; &nbsp; &nbsp;ResNet</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">的核心思想是在网络中引入跨层的直接连接，使得信息可以直接从前一层传递到后一层，即通过跳跃连接将前一层的特征与后一层的特征相加。这种残差连接使得网络可以更轻松地学习恒等映射，从而减轻了训练过程中的梯度消失问题。</span></li><li><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;"> &nbsp; &nbsp; &nbsp; &nbsp;ResNet</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">的基本模块是残差模块（</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">residual block</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">），它由两个卷积层组成，在其中间添加了跳跃连接。这样的设计允许网络在不增加参数和计算复杂度的情况下增加网络深度，从而提高了网络的表示能力。此外，</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">ResNet</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">还采用了批归一化（</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">batch normalization</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">）和全局平均池化（</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">global average pooling</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">）等技术，以进一步提高模型的性能和泛化能力。</span></li><li><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;"> &nbsp; &nbsp; &nbsp; &nbsp;ResNet</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">的不同版本包括</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">ResNet-18</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">、</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">ResNet-34</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">、</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">ResNet-50</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">、</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">ResNet-101</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">和</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">ResNet-152</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">等，它们的层数和参数量不同，可以根据任务的复杂度和资源的限制选择合适的版本。</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">ResNet</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">在图像分类任务中取得了很好的性能，在多个数据集上超越了传统的网络结构，成为了图像分类领域的重要算法之一。</span></li></ul>', NULL, NULL, 't', 'load_from', NULL, 1);
INSERT INTO "public"."am_public_operator" ("id", "version", "app_id", "available", "created_by_user", "created_by_name", "created_date", "last_modified_by_user", "last_modified_by_name", "last_modified_date", "org_id_path", "compute_spec_id", "container_image_id", "container_image_name", "defalut_file", "description", "dev_language_type", "fork_count", "inference_file_path", "is_dierctory", "is_distributed", "is_internet", "is_train_model", "is_train_visual", "model_type", "name", "operator_classify", "operator_type", "p_id", "reality_name", "save_status", "source_id", "source_type", "start_file_path", "summary", "train_break_point", "train_distributed_parameter", "train_log", "train_model_parameter", "train_visual_parameter", "am_operator_parameter_id") VALUES (20, 5, 1, 't', 1, '超级管理员', '2023-10-19 10:31:06.442276', 1, '超级管理员', '2023-12-19 13:49:46.747549', '0/1/', 1, 1, NULL, NULL, NULL, 'PYTHON', 2, '/inference.py', 'f', 'f', NULL, 't', 't', NULL, 'YOLOV5目标检测算法', 'TARGET_DETECTION', 'SYSTEM_PRESET', 6, NULL, 1, NULL, 'LOCAL_UPLOAD', '/train.py', '<p> &nbsp; &nbsp; &nbsp; &nbsp;YOLOv5<span style="font-family: 宋体;">是一种目标检测算法，它是</span>YOLO<span style="font-family: 宋体;">（</span>You Only Look Once<span style="font-family: 宋体;">）系列的版本之一。相比于之前的版本，</span>YOLOv5<span style="font-family: 宋体;">在速度和准确性上都有显著的改进。该算法采用了一种单阶段的检测方法，能够在一次前向传播中同时实现目标的位置定位和分类。</span></p><p><span style="font-family: 宋体;"> &nbsp; &nbsp;其核心思想是将目标检测问题转化为一个回归问题。它通过将图像分成一系列的网格，并在每个网格中预测多个边界框（</span>bounding box<span style="font-family: 宋体;">）和对应的目标类别概率。每个边界框由一组坐标值来表示，包括框的中心坐标、宽度和高度。同时，</span>YOLOv5<span style="font-family: 宋体;">利用卷积神经网络（</span>CNN<span style="font-family: 宋体;">）来提取图像特征，并通过多层感知机（</span>MLP<span style="font-family: 宋体;">）进行分类和回归。</span><br> &nbsp; &nbsp; &nbsp; &nbsp;YOLOv5<span style="font-family: 宋体;">引入了一种轻量级的网络结构，采用了一系列的卷积块和池化层，以提取不同尺度的特征。此外，</span>YOLOv5<span style="font-family: 宋体;">还使用了自适应的锚框（</span>anchor<span style="font-family: 宋体;">）设计，根据训练数据自动调整锚框的大小和比例，以适应不同尺度的目标。</span>YOLOv5<span style="font-family: 宋体;">还具有一些特殊功能，如多尺度训练和推理、数据增强、自动增强策略等，以提高模型的泛化能力和鲁棒性。</span><br> &nbsp; &nbsp; &nbsp; &nbsp;YOLOv5<span style="font-family: 宋体;">在目标检测任务中具有较高的效率和准确性。它能够实时地检测出图像中的多个目标，并给出它们的位置和类别信息。其轻量级的结构和快速的推理速度使得它在嵌入式设备和实时应用中具有广泛的应用前景。</span></p>', NULL, NULL, 't', 'load_from', NULL, 2);
INSERT INTO "public"."am_public_operator" ("id", "version", "app_id", "available", "created_by_user", "created_by_name", "created_date", "last_modified_by_user", "last_modified_by_name", "last_modified_date", "org_id_path", "compute_spec_id", "container_image_id", "container_image_name", "defalut_file", "description", "dev_language_type", "fork_count", "inference_file_path", "is_dierctory", "is_distributed", "is_internet", "is_train_model", "is_train_visual", "model_type", "name", "operator_classify", "operator_type", "p_id", "reality_name", "save_status", "source_id", "source_type", "start_file_path", "summary", "train_break_point", "train_distributed_parameter", "train_log", "train_model_parameter", "train_visual_parameter", "am_operator_parameter_id") VALUES (21, 3, 1, 't', 1, '超级管理员', '1900-01-20 16:26:27.378065', 1, '超级管理员', '1900-01-20 19:01:42.942957', '0/1/', 1, 1, NULL, NULL, NULL, 'PYTHON', 0, '/inference.py', 'f', 'f', NULL, 't', 't', NULL, 'U-Net语义分割算法', 'PICTURE_PARTITION', 'SYSTEM_PRESET', 8, NULL, 1, NULL, 'LOCAL_UPLOAD', '/train.py', '<ul><li><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;"> &nbsp; &nbsp; &nbsp; &nbsp;U-Net</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">是一种经典的语义分割算法，它基于卷积神经网络（</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">CNN</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">）的架构，在医学图像分割等领域取得了广泛的应用。</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">U-Net</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">的特点在于它采用了编码器</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">-</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">解码器结构，能够同时提取高层和低层特征，以实现准确的像素级别分割。</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;"> </span></li><li><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;"> &nbsp; &nbsp; &nbsp; &nbsp;U-Net</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">的架构由两个主要部分组成：编码器和解码器。编码器通过堆叠卷积层和池化层，逐渐减小特征图的尺寸和通道数，以提取抽象的语义特征。解码器则通过上采样和跳跃连接的方式，将编码器的特征逐渐恢复到原始输入图像的尺寸，同时保留了细节信息。这种跳跃连接的设计可以帮助解码器更好地利用低层和高层特征，从而提高分割的准确性。</span></li><li><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;"> &nbsp; &nbsp; &nbsp; </span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">在</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">U-Net</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">中，编码器和解码器之间还存在着相同尺寸的特征图，它们通过跳跃连接连接在一起。这样的设计可以帮助解码器更好地利用编码器中的上下文信息，从而提高分割的精度。此外，</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">U-Net</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">还使用了卷积核为</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">1x1</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">的卷积层来进行通道数的调整和特征的融合，以进一步提升分割的性能。</span></li><li><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;"> &nbsp; &nbsp; &nbsp; &nbsp;U-Net</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">在语义分割任务中具有很好的性能和鲁棒性，尤其适用于小样本和医学图像等领域。其优点在于能够准确地捕捉目标的边界和细节，同时具有较高的计算效率。</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">U-Net</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">的架构也为后续的改进和扩展提供了基础，如改进的</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">U-Net</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">、</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px;">U-Net++</span><span style="color: rgb(54, 74, 99); background-color: rgb(255, 255, 255); font-size: 14px; font-family: 宋体;">等。</span></li></ul>', NULL, NULL, 't', 'load_from', NULL, 3);
