INSERT INTO "public"."mine_operator" ("id", "version", "app_id", "available", "created_by_user", "created_by_name", "created_date", "last_modified_by_user", "last_modified_by_name", "last_modified_date", "org_id_path", "apply_id", "classify", "compute_spec_id", "description", "dev_language_type", "file_path", "is_dierctory", "is_distributed", "is_release", "model_type", "name", "operator_type", "p_id", "save_status", "source_id", "source_type", "spt_type", "start_file_path", "summary") VALUES (543, 3, 1, 't', 1, '超级管理员', '2023-11-21 17:05:23.432109', 1, '超级管理员', '2024-02-01 09:21:28.898078', '0/1/', NULL, 'f', NULL, '线性支持向量机回归模型，L2正则化。使用随机梯度下降（Stochastic Gradient Descend， SGD）方法优化L1损失函数。', 'PYTHON', '/algplat/operator/algplat/one/mine/243', 'f', NULL, 2, 'DRAG_MODELING', 'SVM回归', 'SYSTEM_PRESET', 46, NULL, NULL, 'LOCAL_UPLOAD', 'FLINK', 'LinearSVR', '<ul><li><strong>1.概述</strong><br/>&nbsp;&nbsp;&nbsp;&nbsp;算子名称：SVM回归<br/>&nbsp;&nbsp;&nbsp;&nbsp;算子描述:线性支持向量机回归模型，L2正则化。使用随机梯度下降（Stochastic Gradient Descend， SGD）方法优化L1损失函数。<br/></li></ul><ul><li><strong>2.输入</strong><br/>&nbsp;&nbsp;&nbsp;&nbsp映射名称:data_in_0<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp代码参数:data_in_0<br/><strong>3.输出</strong><br/>&nbsp;&nbsp;&nbsp;&nbsp;映射名称:model_out_0<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;代码参数:model_out_0<br/>&nbsp;&nbsp;&nbsp;&nbsp;映射名称:data_out_0<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;代码参数:data_out_0<br/><strong>4.参数</strong><br/>&nbsp;&nbsp;&nbsp;&nbsp;用户填写参数名:max_iter<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数描述:数据集最大迭代次数（注意：非SGD步数）<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数说明：<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;类型:INTEGER<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;默认值:100<br/>&nbsp;&nbsp;&nbsp;&nbsp;用户填写参数名:tol<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数描述:说明见n_step_no_change参数。<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数说明：<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;类型:FLOAT<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;默认值:0.001<br/>&nbsp;&nbsp;&nbsp;&nbsp;用户填写参数名:n_step_no_change<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数描述:如果迭代时连续n_step_no_change步内(loss - best_loss) >= tol，则停止迭代；如果n_step_no_change小于等于0，则不会停止迭代，模型将迭代max_iter次。<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数说明：<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;类型:INTEGER<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;默认值:0<br/>&nbsp;&nbsp;&nbsp;&nbsp;用户填写参数名:minibatch_size<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数描述:SGD每步使用的样本数量。<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数说明：<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;类型:INTEGER<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;范围:1.0-null<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;默认值:1<br/>&nbsp;&nbsp;&nbsp;&nbsp;用户填写参数名:alpha<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数描述:L2正则化系数。<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数说明：<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;类型:FLOAT<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;默认值:0.0001<br/>&nbsp;&nbsp;&nbsp;&nbsp;用户填写参数名:labelCol<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数描述:标签列列名<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数说明：<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;类型:STRING<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;默认值:label<br/>&nbsp;&nbsp;&nbsp;&nbsp;用户填写参数名:featuresCol<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数描述:特征列的列名<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数说明：<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;类型:STRING<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;默认值:features<br/>&nbsp;&nbsp;&nbsp;&nbsp;用户填写参数名:predictionCol<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数描述:输出预测结果列的列名<br/>&nbsp;&nbsp;&nbsp;&nbsp;参数说明：<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;类型:STRING<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;默认值:prediction<br/></li></ul>');
