INSERT INTO "public"."mine_operator" ("id", "version", "app_id", "available", "created_by_user", "created_by_name", "created_date", "last_modified_by_user", "last_modified_by_name", "last_modified_date", "org_id_path", "apply_id", "classify", "compute_spec_id", "description", "dev_language_type", "file_path", "is_dierctory", "is_distributed", "is_release", "model_type", "name", "operator_type", "p_id", "save_status", "source_id", "source_type", "spt_type", "start_file_path", "summary") VALUES (318, 6, 1, 't', 1, '超级管理员', '2023-07-11 16:10:39.346111', 1, '超级管理员', '2024-02-01 09:46:36.142048', '0/1/', NULL, 't', NULL, '向量选择是一个可以将向量转换成新特征子数组的转换器。它可以用来从一个向量中提取部分特征构成新的特征,这对提取给定向量中的有效特征非常有用。
